<script>
const $sectionPin = document.querySelector('#sectionPin');
const $pinWrapSticky = document.querySelector('.pin-wrap-sticky');
const $pinWrap = document.querySelector('.pin-wrap');

// Detect if the device is mobile
const isMobile = window.innerWidth <= 768;

// Adjust heights dynamically for better scaling
$sectionPin.style.height = isMobile ? '200vh' : '250vh'; 
$pinWrapSticky.style.height = isMobile ? '60vh' : '80vh';
$pinWrapSticky.style.width = '100vw';
$pinWrapSticky.style.position = 'sticky';
$pinWrapSticky.style.top = '0';
$pinWrapSticky.style.overflow = 'hidden';

$pinWrap.style.height = isMobile ? '60vh' : '80vh';
$pinWrap.style.width = isMobile ? '150vw' : '200vw';

// Ensure sticky functionality and horizontal scrolling
if ('ViewTimeline' in window) {
  $pinWrap.animate(
    {
      transform: [``, `translateX(calc(-100% + 100vw))`],
    },
    {
      timeline: new ViewTimeline({
        subject: $sectionPin,
        axis: 'block',
      }),
      fill: 'forwards',
      rangeStart: `contain 0%`,
      rangeEnd: `contain 100%`,
    }
  );
} else {
  let isScrolling = false;

  // Define missing variables
  const stickyPanelTop = $pinWrapSticky.offsetTop; // Get the top position of the sticky panel
  const stickyPanelHeight = $pinWrapSticky.offsetHeight; // Get the height of the sticky panel
  const sectionHeight = $sectionPin.offsetHeight; // Get the height of the entire section

  window.addEventListener('scroll', () => {
    const scrollY = window.scrollY;

    // Calculate scrollable range
    const maxScroll = sectionHeight - stickyPanelHeight;

    if (scrollY >= stickyPanelTop && scrollY < stickyPanelTop + maxScroll) {
      // Calculate scroll percentage
      const scrollPercentage = (scrollY - stickyPanelTop) / maxScroll;
      const transformValue = `translateX(${(-100 * scrollPercentage)}vw)`;
      $pinWrap.style.transform = transformValue;

      // Apply transition for smooth movement while scrolling
      $pinWrap.style.transition = 'transform 0.3s ease-out'; 
    } else if (scrollY >= stickyPanelTop + maxScroll) {
      // Ensure it stays at the final position
      $pinWrap.style.transform = `translateX(-100vw)`;
      $pinWrap.style.transition = 'transform 0.5s ease-out';
    } else {
      // Reset to starting position when not in the scroll range
      $pinWrap.style.transform = 'translateX(0)';
      $pinWrap.style.transition = 'none';
    }
  });
}
</script>


<script>
// Create cursor elements
const cursor = document.createElement("div");
cursor.classList.add("laser-pointer");
document.body.appendChild(cursor);

const trail = document.createElement("div");
trail.classList.add("cursor-trail");
document.body.appendChild(trail);

// Track mouse movement
let timeout;
document.addEventListener("mousemove", (e) => {
    // Move small cursor instantly
    cursor.style.left = `${e.clientX}px`;
    cursor.style.top = `${e.clientY}px`;

    // Move trailing circle with delay
    setTimeout(() => {
        trail.style.left = `${e.clientX + 20}px`;
        trail.style.top = `${e.clientY + 20}px`;
        trail.style.opacity = 1;
    }, 100);

    // Hide trail after 1s of no movement
    clearTimeout(timeout);
    timeout = setTimeout(() => {
        trail.style.opacity = 0;
    }, 1000);
});

// Add hover effect for interactive elements
document.querySelectorAll("a, button, img").forEach((element) => {
    element.addEventListener("mouseenter", () => {
        cursor.classList.add("hover-effect"); // Expand cursor
    });
    element.addEventListener("mouseleave", () => {
        cursor.classList.remove("hover-effect"); // Revert cursor
    });
});

</script>

